mapper.select:
  select
  e.fireId as fireId,
  e.jobName as jobName,
  e.startTime as startTime,
  e.endTime as endTime,
  e.execMethod as execMethod,
  e.`status` as status,
  e.executionConfiguration as executionConfiguration,
  e.executionLog as executionLog,
   e.type as type
from (select * from task_execution_log ) e WHERE 1=1

      and  e.jobName IN
      (
          select  d.TASK_NAME from d_task_group_member_attribute d
          WHERE d.TASK_GROUP_NAME IN
          (
              select TASK_GROUP_NAME from d_user_group_tasks_relation
              WHERE USER_GROUP_NAME=#{6}
          )
      )


      and e.status = #{2}


      and e.type = #{3}


      and date_format(e.startTime, '%Y-%m-%d') = #{4}


      and e.jobName = #{5}

  ORDER BY endTime DESC
limit #{0},#{1}

SELECT  COUNT(1) FROM task_execution_log e WHERE 1=1

      and  e.jobName IN
      (
          select  d.TASK_NAME from d_task_group_member_attribute d
          WHERE d.TASK_GROUP_NAME IN
          (
              select TASK_GROUP_NAME from d_user_group_tasks_relation
              WHERE USER_GROUP_NAME=#{4}
          )
      )


      and e.status = #{0}


      and e.type = #{1}


      and date_format(e.startTime, '%Y-%m-%d') = #{2}


      and e.jobName = #{3}
