mapper.select:
select j.name as name, j.ID_DIRECTORY as directoryId, j.ID_JOB as jobId, ifnull(usr.NAME,j.CREATED_USER) as createUser, j.CREATED_DATE as createDate, ifnull(usr_m.NAME,j.MODIFIED_USER) as modifiedUser, j.MODIFIED_DATE as modifiedDate from (SELECT * FROM r_job ORDER BY CREATED_DATE DESC) j left join r_user usr on j.CREATED_USER = usr.ID_USER left join r_user usr_m on j.MODIFIED_USER = usr_m.ID_USER WHERE 1=1
AND j.`NAME` IN ( select DISTINCT TASK_NAME from d_task_group_member_attribute where TASK_GROUP_NAME in ( select TASK_GROUP_NAME from d_user_group_tasks_relation where USER_GROUP_NAME=#{2} ) AND TYPE="job" )
limit #{0},#{1}

select j.name as name, j.ID_JOB as jobId, j.ID_DIRECTORY as directoryId, j.CREATED_USER as createUser, j.CREATED_DATE as createDate, j.MODIFIED_USER as modifiedUser, j.MODIFIED_DATE as modifiedDate from r_job j where 1=1
AND j.`NAME` IN ( select DISTINCT TASK_NAME from d_task_group_member_attribute where TASK_GROUP_NAME in ( select TASK_GROUP_NAME from d_user_group_tasks_relation where USER_GROUP_NAME=#{4} ) AND TYPE="job" )
and j.name LIKE concat('%',#{2},'%')
and date_format(j.CREATED_DATE, '%Y-%m-%d') = #{3}
and j.CREATED_USER LIKE concat('%',#{5},'%')
limit #{0},#{1}

select count(1) from  r_job j where 1=1

    AND j.`NAME` IN
    (
        select DISTINCT TASK_NAME from d_task_group_member_attribute where TASK_GROUP_NAME in
            (
            select TASK_GROUP_NAME from
            d_user_group_tasks_relation where USER_GROUP_NAME=#{2}
            )
        AND TYPE="job"
    )


    and j.name LIKE concat('%',#{0},'%')


    and date_format(j.CREATED_DATE, '%Y-%m-%d') = #{1}
