mapper.select:
SELECT d.ID_JOBTASK AS idJobtask, d.ID_JOB AS idJob, job.NAME AS jobName, d.SLAVES AS slaves, d.ISREPEAT AS isrepeat, d.SCHEDULERTYPE AS schedulertype, d.INTERVALMINUTES AS intervalminutes, d.`HOUR` AS hour, d.MINUTES AS minutes, d.WEEKDAY AS weekday, d.DAYOFMONTH AS dayofmonth, d.executionConfig AS executionConfig, d.username AS username, d.month AS MONTH FROM d_timerscheduler d, r_job job WHERE d.ID_JOB = job.ID_JOB
and job.NAME IN ( select d.TASK_NAME from d_task_group_member_attribute d WHERE d.TASK_GROUP_NAME IN ( select TASK_GROUP_NAME from d_user_group_tasks_relation WHERE USER_GROUP_NAME=#{5} ) and type="job" )
and d.SCHEDULERTYPE = #{2}
and d.SLAVES = #{3}
and job.NAME LIKE concat('%',#{4},'%')
limit #{0},#{1}

select count(1) from d_timerscheduler d WHERE 1=1

   and  d.JOB_NAME IN
   (
       select  d.TASK_NAME from d_task_group_member_attribute d
       WHERE d.TASK_GROUP_NAME IN
       (
           select TASK_GROUP_NAME from d_user_group_tasks_relation
           WHERE USER_GROUP_NAME=#{3}
       ) and type="job"
   )


   and  d.SCHEDULERTYPE = #{0}


   and d.SLAVES = #{1}


   and d.JOB_NAME LIKE concat('%',#{2},'%')

mapper.update:
UPDATE d_timerscheduler
INTERVALMINUTES=#{intervalminutes}, SCHEDULERTYPE=#{schedulertype}, d_timerscheduler.`HOUR`=#{hour}, MINUTES=#{minutes}, WEEKDAY=#{weekday}, DAYOFMONTH=#{dayofmonth}, month=#{month}
WHERE ID_JOBTASK=#{idJobtask}
