mapper.select:
select g.ID_USER_GROUP as userGroupId, g.USER_GROUP_NAME as userGroupName, g.USER_GROUP_DESC as userGroupDesc from d_user_group g where 1=1
and g.USER_GROUP_NAME LIKE concat('%',#{2},'%')
limit #{0},#{1}

mapper.update:
UPDATE d_user_group
USER_GROUP_NAME=#{userGroupName}, USER_GROUP_DESC=#{userGroupDesc},
WHERE ID_USER_GROUP=#{userGroupId}

mapper.select:
select g.ID_USER_GROUP as userGroupId, g.USER_GROUP_NAME as userGroupName, g.USER_GROUP_DESC as userGroupDesc from d_user_group g WHERE 1=1
AND g.USER_GROUP_NAME=#{0}
AND g.ID_USER_GROUP=#{1}

mapper.update:
UPDATE

    d_user_group_member_attribute


    d_user_group_slaves_relation


